<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');    class Model_clientes extends CI_Model {        public function getClientes($codEmpresa){            return $this->db->select("c.*, p.descricao as descricaoPlano") //                ->from("cliente c")                ->join("contrato con", "con.codCliente = c.codCliente","left")                ->join("plano p", "p.codPlano = con.codPlano","left")                ->where("c.codEmpresa", $codEmpresa)                ->where("c.codCliente>", 0)            ->get();        }                 public function Inserir($parametros){            $this->db->insert("cliente", $parametros);            return $this->db->insert_id();        }                public function getClienteByName($nome, $codEmpresa){                        return $this->db->get_where("cliente", array("nome" => $nome, "codEmpresa" => $codEmpresa));                    }                public function getCliente($codCliente){            return $this->db->select("cli.*, c.descricao as cidade, e.sigla")                    ->from("cliente cli")                    ->join("cidade c", "c.codCidade = cli.codCidade")                    ->join("estado e", "e.codEstado = c.codEstado")                    ->where("cli.codCliente",$codCliente)                    ->get();        }                            public function aniversariantes($codEmpresa){            return $this->db->select("*")                    ->from("cliente")                    ->where("DATE_FORMAT(nascimento,'%m-%d') = DATE_FORMAT(CURDATE(),'%m-%d')", null)                    ->where("codEmpresa", $codEmpresa)                    ->where("codCliente>", 0)                    ->get();                            }        public function getDependentes($codEmpresa){            return $this->db->select("dep.*")                    ->from("dependente dep")                    ->join("contrato cont", "cont.codContrato = dep.codContrato")                    ->where("cont.codEmpresa",$codEmpresa)                    ->get();        }}